import React, { useState, useEffect, useCallback } from 'react';
import { User, Package, Heart, Settings, Mail, Phone, Star, Bell, ShoppingCart, Plus, Eye, QrCode, Search, X } from 'lucide-react';
import apiService from '../services/api';

const UserDropdowns = ({ user }) => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orders, setOrders] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(false);
  const API_URL = process.env.REACT_APP_API_URL;
  const [ordersLoading, setOrdersLoading] = useState(false);

  const handleMouseEnter = (dropdown) => {
    setActiveDropdown(dropdown);
  };

  const handleMouseLeave = () => {
    setActiveDropdown(null);
  };

  const loadProducts = async () => {
    try {
      setLoading(true);
      const response = await apiService.getProducts({ limit: 50 }); // Augmenter la limite pour avoir plus de produits
      const productsData = response?.data || [];
      setProducts(productsData);
      setFilteredProducts(productsData);
    } catch (error) {
      console.error('Erreur lors du chargement des produits:', error);
      setProducts([]);
      setFilteredProducts([]);
    } finally {
      setLoading(false);
    }
  };

  const loadOrdersAndReservations = async () => {
    try {
      setOrdersLoading(true);
      // Charger les commandes
      const ordersResponse = await apiService.getOrders();
      const userOrders = (ordersResponse || []).filter(order => 
        order.userId === user?._id || order.user?._id === user?._id
      ).slice(0, 5); // Limiter à 5 commandes récentes
      setOrders(userOrders);

      // Charger les réservations
      const reservationsResponse = await apiService.getReservations();
      const userReservations = (reservationsResponse || []).filter(reservation => 
        reservation.userId === user?._id || reservation.user?._id === user?._id
      ).slice(0, 3); // Limiter à 3 réservations récentes
      setReservations(userReservations);
    } catch (error) {
      console.error('Erreur lors du chargement des commandes et réservations:', error);
      setOrders([]);
      setReservations([]);
    } finally {
      setOrdersLoading(false);
    }
  };

  // Fonction de recherche de produits
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    if (term === '') {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter(product =>
        product.name.toLowerCase().includes(term) ||
        product.description?.toLowerCase().includes(term) ||
        product.category?.toLowerCase().includes(term)
      );
      setFilteredProducts(filtered);
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setFilteredProducts(products);
  };

  const loadOrdersAndReservationsCallback = useCallback(loadOrdersAndReservations, [user?._id]);

  useEffect(() => {
    if (activeDropdown === 'products') {
      loadProducts();
      setSearchTerm(''); // Reset search when opening
    } else if (activeDropdown === 'orders') {
      loadOrdersAndReservationsCallback();
    }
  }, [activeDropdown, user?._id, loadOrdersAndReservationsCallback]);

  const userProfileData = {
    fullName: `${user?.prenom || ''} ${user?.nom || ''}`.trim() || 'Utilisateur',
    email: user?.email || 'email@example.com',
    phone: user?.telephone || '+224623841149'
  };

  // Fonction pour formater le statut des commandes
  const getOrderStatusLabel = (status) => {
    const statusMap = {
      'pending': 'En attente',
      'confirmed': 'Confirmée',
      'processing': 'En cours',
      'shipped': 'Expédiée',
      'delivered': 'Livrée',
      'cancelled': 'Annulée',
      'completed': 'Terminée'
    };
    return statusMap[status] || status;
  };

  // Fonction pour formater le statut des réservations
  const getReservationStatusLabel = (status) => {
    const statusMap = {
      'pending': 'En attente',
      'confirmed': 'Confirmée',
      'ready': 'Prête',
      'collected': 'Retirée',
      'cancelled': 'Annulée',
      'expired': 'Expirée'
    };
    return statusMap[status] || status;
  };


  return (
    <div className="flex items-center space-x-6">
      {/* Mon Profil */}
      <div 
        className="relative"
        onMouseEnter={() => handleMouseEnter('profile')}
        onMouseLeave={handleMouseLeave}
      >
        <button className="flex items-center space-x-2 text-gray-700 hover:text-primary-600 transition-colors">
          <User className="h-5 w-5" />
          <span className="text-sm font-medium">Mon profil</span>
        </button>

        {activeDropdown === 'profile' && (
          <div className="absolute top-full right-0 mt-2 w-80 bg-white border border-gray-200 rounded-lg shadow-lg z-50">
            <div className="p-4">
              <div className="flex items-center space-x-4 mb-4">
                <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center overflow-hidden">
                  <User className="h-10 w-10 text-gray-400" />
                </div>
                <div className="flex-1">
                  <h4 className="font-medium text-gray-900">{userProfileData.fullName}</h4>
                  <p className="text-sm text-gray-500">Client fidèle</p>
                </div>
              </div>

              <div className="space-y-3">
                <div className="flex items-center space-x-3 text-sm">
                  <Mail className="h-4 w-4 text-gray-400" />
                  <span className="text-gray-700">{userProfileData.email}</span>
                </div>
                <div className="flex items-center space-x-3 text-sm">
                  <Phone className="h-4 w-4 text-gray-400" />
                  <span className="text-gray-700">{userProfileData.phone}</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Mes Commandes */}
      <div 
        className="relative"
        onMouseEnter={() => handleMouseEnter('orders')}
        onMouseLeave={handleMouseLeave}
      >
        <button className="flex items-center space-x-2 text-gray-700 hover:text-primary-600 transition-colors">
          <Package className="h-5 w-5" />
          <span className="text-sm font-medium">Mes commandes</span>
        </button>

        {activeDropdown === 'orders' && (
          <div className="absolute top-full right-0 mt-2 w-96 bg-white border border-gray-200 rounded-lg shadow-lg z-50">
            <div className="p-4">
              <h4 className="font-medium text-gray-900 mb-4">Historique des commandes</h4>
              {ordersLoading ? (
                <div className="flex items-center justify-center h-32">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary-600"></div>
                </div>
              ) : (
                <>
                  <div className="space-y-3 max-h-64 overflow-y-auto">
                    {orders.length > 0 ? orders.map((order) => (
                      <div key={order._id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                        <div>
                          <p className="text-sm font-medium text-gray-900">Commande #{order._id?.slice(-6) || 'N/A'}</p>
                          <p className="text-xs text-gray-500">{new Date(order.createdAt).toLocaleDateString('fr-FR')}</p>
                        </div>
                        <div className="text-right">
                          <p className="text-sm font-medium text-gray-900">{order.totalAmount || 0} GNF</p>
                          <span className={`text-xs px-2 py-1 rounded-full ${
                            order.status === 'delivered' || order.status === 'completed' ? 'bg-green-100 text-green-800' :
                            order.status === 'processing' || order.status === 'confirmed' ? 'bg-blue-100 text-blue-800' :
                            order.status === 'cancelled' ? 'bg-red-100 text-red-800' :
                            'bg-yellow-100 text-yellow-800'
                          }`}>
                            {getOrderStatusLabel(order.status)}
                          </span>
                        </div>
                      </div>
                    )) : (
                      <div className="text-center py-8 text-gray-500">
                        <Package className="h-8 w-8 mx-auto mb-2 text-gray-400" />
                        <p className="text-sm">Aucune commande trouvée</p>
                      </div>
                    )}
                  </div>

                  <div className="mt-4 pt-3 border-t border-gray-200">
                    <h5 className="font-medium text-gray-900 mb-2">Réservations</h5>
                    <div className="space-y-2">
                      {reservations.length > 0 ? reservations.map((reservation) => (
                        <div key={reservation._id} className="flex items-center justify-between text-sm">
                          <span className="text-gray-700">{reservation.productId?.name || reservation.product?.name || 'Produit inconnu'}</span>
                          <span className={`px-2 py-1 rounded-full text-xs ${
                            reservation.status === 'confirmed' ? 'bg-blue-100 text-blue-800' :
                            reservation.status === 'collected' ? 'bg-green-100 text-green-800' :
                            reservation.status === 'ready' ? 'bg-yellow-100 text-yellow-800' :
                            reservation.status === 'cancelled' ? 'bg-red-100 text-red-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {getReservationStatusLabel(reservation.status)}
                          </span>
                        </div>
                      )) : (
                        <div className="text-center py-4 text-gray-500">
                          <p className="text-sm">Aucune réservation trouvée</p>
                        </div>
                      )}
                    </div>
                  </div>
                </>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Produits */}
      <div 
        className="relative"
        onMouseEnter={() => handleMouseEnter('products')}
        onMouseLeave={handleMouseLeave}
      >
        <button className="flex items-center space-x-2 text-gray-700 hover:text-primary-600 transition-colors">
          <Package className="h-5 w-5" />
          <span className="text-sm font-medium">Produits</span>
        </button>

        {activeDropdown === 'products' && (
          <div className="fixed inset-0 bg-black bg-opacity-20 z-50 flex items-center justify-center p-4">
            <div className="bg-white rounded-2xl shadow-2xl w-full max-w-7xl max-h-[90vh] overflow-hidden">
              <div className="p-6 border-b border-gray-200">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-2xl font-bold text-gray-900">Nos Produits</h2>
                  <div className="flex items-center space-x-4">
                    <span className="bg-primary-100 text-primary-800 px-3 py-1 rounded-full text-sm font-medium">
                      {filteredProducts.length} produits {searchTerm ? 'trouvés' : 'disponibles'}
                    </span>
                  </div>
                </div>

                {/* Barre de recherche */}
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Search className="h-5 w-5 text-gray-400" />
                  </div>
                  <input
                    type="text"
                    placeholder="Rechercher des produits..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="block w-full pl-10 pr-10 py-3 border border-gray-300 rounded-xl leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-2 focus:ring-primary-500 focus:border-primary-500 text-sm"
                  />
                  {searchTerm && (
                    <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                      <button
                        onClick={clearSearch}
                        className="text-gray-400 hover:text-gray-600 transition-colors"
                      >
                        <X className="h-5 w-5" />
                      </button>
                    </div>
                  )}
                </div>
              </div>

              <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
                {loading ? (
                  <div className="flex items-center justify-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
                  </div>
                ) : filteredProducts.length === 0 && searchTerm ? (
                    <div className="text-center py-12">
                      <Package className="h-16 w-16 mx-auto text-gray-400 mb-4" />
                      <h3 className="text-lg font-medium text-gray-900 mb-2">Aucun produit trouvé</h3>
                      <p className="text-gray-500">Essayez avec d'autres mots-clés</p>
                      <button
                        onClick={clearSearch}
                        className="mt-4 bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 transition-colors"
                      >
                        Voir tous les produits
                      </button>
                    </div>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                    {filteredProducts.map((product) => (
                      <div key={product._id} className="group relative bg-white rounded-2xl border border-gray-200 overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2">
                        {/* Image Container */}
                        <div className="relative h-48 bg-gradient-to-br from-green-50 to-emerald-100 overflow-hidden">
                          {product.image ? (
                            <img 
                              src={`${process.env.REACT_APP_API_URL}${product.image}`}
                              alt={product.name}
                              className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                              onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/300x200?text=Produit" }}
                            />
                          ) : (
                            <div className="w-full h-full flex items-center justify-center">
                              <Package className="h-16 w-16 text-gray-400" />
                            </div>
                          )}

                          {/* Overlay on hover */}
                          <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 transition-all duration-300 flex items-center justify-center">
                            <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex space-x-3">
                              <button className="bg-white text-gray-900 p-3 rounded-full hover:bg-primary-600 hover:text-white transition-colors duration-200">
                                <Eye className="h-5 w-5" />
                              </button>
                              <button className="bg-white text-gray-900 p-3 rounded-full hover:bg-green-600 hover:text-white transition-colors duration-200">
                                <ShoppingCart className="h-5 w-5" />
                              </button>
                              <button className="bg-white text-gray-900 p-3 rounded-full hover:bg-blue-600 hover:text-white transition-colors duration-200">
                                <QrCode className="h-5 w-5" />
                              </button>
                            </div>
                          </div>


                          {/* Discount badge */}
                          {product.discount > 0 && (
                            <div className="absolute top-3 right-3">
                              <span className="bg-red-500 text-white px-2 py-1 rounded-full text-xs font-bold">
                                -{product.discount}%
                              </span>
                            </div>
                          )}
                        </div>

                        {/* Content */}
                        <div className="p-4">
                          <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-primary-600 transition-colors">
                            {product.name}
                          </h3>

                          <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                            {product.description || 'Description non disponible'}
                          </p>

                          <div className="flex items-center justify-between mb-3">
                            <div className="flex flex-col">
                              {product.discount > 0 ? (
                                <>
                                  <span className="text-lg font-bold text-primary-600">
                                    {(product.price * (1 - product.discount / 100)).toFixed(0)} GNF
                                  </span>
                                  <span className="text-sm text-gray-500 line-through">
                                    {product.price} GNF
                                  </span>
                                </>
                              ) : (
                                <span className="text-lg font-bold text-primary-600">
                                  {product.price} GNF
                                </span>
                              )}
                            </div>

                            <div className="flex items-center space-x-1">
                              {[...Array(5)].map((_, i) => (
                                <Star key={i} className={`h-4 w-4 ${
                                  i < (product.rating || 4) ? 'text-yellow-400 fill-current' : 'text-gray-300'
                                }`} />
                              ))}
                            </div>
                          </div>

                          {/* Action buttons - visible on hover */}
                          <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                            <div className="flex space-x-2">
                              <button className="flex-1 bg-primary-600 text-white py-2 px-3 rounded-lg hover:bg-primary-700 transition-colors duration-200 flex items-center justify-center space-x-2">
                                <Plus className="h-4 w-4" />
                                <span className="text-sm font-medium">Ajouter</span>
                              </button>
                              <button className="bg-gray-100 text-gray-700 py-2 px-3 rounded-lg hover:bg-gray-200 transition-colors duration-200">
                                <Heart className="h-4 w-4" />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )
                }
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Paramètres */}
      <div 
        className="relative"
        onMouseEnter={() => handleMouseEnter('settings')}
        onMouseLeave={handleMouseLeave}
      >
        <button className="flex items-center space-x-2 text-gray-700 hover:text-primary-600 transition-colors">
          <Settings className="h-5 w-5" />
          <span className="text-sm font-medium">Paramètres</span>
        </button>

        {activeDropdown === 'settings' && (
          <div className="absolute top-full right-0 mt-2 w-72 bg-white border border-gray-200 rounded-lg shadow-lg z-50">
            <div className="p-4">
              <h4 className="font-medium text-gray-900 mb-4">Paramètres</h4>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Bell className="h-4 w-4 text-gray-400" />
                    <span className="text-sm text-gray-700">Notifications</span>
                  </div>
                  <label className="relative inline-flex items-center cursor-pointer">
                    <input type="checkbox" className="sr-only peer" defaultChecked />
                    <div className="w-9 h-5 bg-gray-200 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-4 after:w-4 after:transition-all peer-checked:bg-primary-600"></div>
                  </label>
                </div>

                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Mail className="h-4 w-4 text-gray-400" />
                    <span className="text-sm text-gray-700">Emails promotionnels</span>
                  </div>
                  <label className="relative inline-flex items-center cursor-pointer">
                    <input type="checkbox" className="sr-only peer" />
                    <div className="w-9 h-5 bg-gray-200 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-4 after:w-4 after:transition-all peer-checked:bg-primary-600"></div>
                  </label>
                </div>

                <div className="pt-3 border-t border-gray-200">
                  <h5 className="text-sm font-medium text-gray-900 mb-2">Préférences</h5>
                  <div className="space-y-2">
                    <button className="w-full text-left text-sm text-gray-700 hover:text-primary-600 py-1">
                      Langue et région
                    </button>
                    <button className="w-full text-left text-sm text-gray-700 hover:text-primary-600 py-1">
                      Confidentialité
                    </button>
                    <button className="w-full text-left text-sm text-gray-700 hover:text-primary-600 py-1">
                      Sécurité du compte
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDropdowns;
